INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('b9a4b997-e836-486f-888c-c12c29cafd83', 'Создание рабочего места', 'Для создания рабочего места перейдите в пункт меню "Рабочие места", выберите необходимую информацию, выберите сотрудника, нажмите кнопку "Сохранить".', 'https://www.youtube.com/embed/IEQ8cPyPycQ', null, 'helper', 7, 'ru') ON CONFLICT (id) DO NOTHING;
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('4f66db76-bd28-43a6-91d6-26b0a7b05537', 'Cоздание филиала', 'Для создание филиала выбирите компанию для которой вы хотите создать филиал, нажмите на кнопку "Создать филиал", введите необходимую информацию, нажмите кнопку "Сохранить".
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('89d9ffe5-34d0-4173-96ee-67facff16a2c', 'Cоздание компании', 'Для создание компании перейдите в пункт меню "Мои компании" нажмите  на кнопку "Добавить новую компанию", введите необходимую информацию, нажмите кнопку "Сохранить".
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('72b9193b-eded-48b8-99b9-11a77d97bfda', 'Создание пакета услуг', 'Для формирование пакета выберите вкладку "Пакет услуг", выберите услуги которые входят в пакет, укажите скидку, длительность выполнения пакета услуг, стоимость пакета услуг, нажмите кнопку "Сохранить".', 'https://www.youtube.com/embed/HtDfNIXaOeo', null, 'helper', 5, 'ru') ON CONFLICT (id) DO NOTHING;
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('54385c15-e8a7-452a-8249-7a78779bae53', 'Pежим работы филиала', 'Для заполнения режима работы филиала выбирите филиал для которого вы хотите создать расписание, выбирите вкладку "Расписание",введите необходимую информацию, нажмите кнопку "Сохранить".
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('b8520cdb-8139-4f14-a8f8-aad38b90ede0', 'Добавления услуги', 'Для добавления услуг выбирите вкладку "Услуги", введите необходимую информацию, нажмите кнопку "Сохранить основную информацию", в пункте "Дополнительна информация" вы можете добавить атрибуты, нажмите кнопку "Сохранить".
INSERT INTO karma.information (id, title, description, video_url, image_url, tag, index, iso_code) VALUES ('e9280fe4-322a-4355-b89c-e04832ceae55', 'Создание сотрудника', 'Для добавления сотрудника перейдите в пункт меню "Сотрудники", выберите компанию, выберите филиал, нажмите кнопку "Создать сотрудника", введите основную информацию о сотруднике, время работы сотрудника.
